import "./FormStyles.css";

import React, { useRef, useState } from 'react';
import emailjs from '@emailjs/browser';
import toast, { Toaster } from 'react-hot-toast';


const Form = () => {

  const form = useRef(0);

  const [userName, setUserName] = useState(null)
  const [userEmail, setUserEmail] = useState(null)
  const [userSubject, setUserSubject] = useState(null)
  const [userMessage, setUserMessage] = useState(null)

  const handleSubmit = (e) => {
    e.preventDefault();
    if(form.current === undefined){
      handleEmpty();
    }
    else{
      emailjs.sendForm('service_pqzbj3n', 'template_i03ykvt', form.current, 'HxNRmc_Rnj5VGBvAD')
      .then((result) => {
          toast.success('Message envoyé, à bientôt !')
          // form.reset()
      }, (error) => {
        toast.error('Échec de l\'envoi, oups..')
        });
  }}

  return (
    <div>
      <Toaster />
        <div className="form">
          <form ref={form} onSubmit={handleSubmit}>
              <label>Votre nom</label>
              <input type="text" name="user_name" ></input>
              <div className={empty}>Veuillez remplir le champ</div>

              <label>Votre email</label>
              <input type="email" name="user_email" ></input>
              <div className={empty}>Veuillez remplir le champ</div>

              <label>Sujet</label>
              <input type="text" name="subject" ></input>
              <div className={empty}>Veuillez remplir le champ</div>

              <label>Message</label>
              <textarea rows="6" name="message" />
              <div className={empty}>Veuillez remplir le champ</div>

              <button className="btn" onClick={() => window.scroll(0,0)}>Envoyer</button>
          </form>        
        </div>
    </div>
  )
  };

export default Form
