import "./FormStyles.css";

import React, { useRef, useState } from 'react';
import emailjs from '@emailjs/browser';
import toast, { Toaster } from 'react-hot-toast';


const Form = () => {

  const form = useRef(0);

  const [userName, setUserName] = useState(0)
  const [userEmail, setUserEmail] = useState(0)
  const [userSubject, setUserSubject] = useState(0)
  const [userMessage, setUserMessage] = useState(0)

  const [empty, setEmpty] = useState("empty")
  const handleEmpty = () => setEmpty("visible")

  const handleSubmit = (e) => {
    e.preventDefault();
    if(userName == 0 || userName == null){

    }
    //else{
      emailjs.sendForm('service_pqzbj3n', 'template_i03ykvt', form.current, 'HxNRmc_Rnj5VGBvAD')
      .then((result) => {
          toast.success('Message envoyé, à bientôt !')
          // form.reset()
      }, (error) => {
        toast.error('Échec de l\'envoi, oups..')
        });
      }
    //}

  return (
    <div>
      <Toaster />
        <div className="form">
          <form ref={form} onSubmit={handleSubmit}>
              <label>Votre nom</label>
              <input type="text" name="user_name" onChange={(e) => setUserName(e.target.value)}></input>
              <div className={empty}>Veuillez remplir le champ</div>

              <label>Votre email</label>
              <input type="email" name="user_email" onChange={(e) => setUserEmail(e.target.value)} ></input>
              <div className={empty}>Veuillez remplir le champ</div>

              <label>Sujet</label>
              <input type="text" name="subject" onChange={(e) => setUserSubject(e.target.value)}></input>
              <div className={empty}>Veuillez remplir le champ</div>

              <label>Message</label>
              <textarea rows="6" name="message" onChange={(e) => setUserMessage(e.target.value)}/>
              <div className={empty}>Veuillez remplir le champ</div>

              <button className="btn" onClick={() => window.scroll(0,0)}>Envoyer</button>
          </form>        
        </div>
    </div>
  )
  };

export default Form
